mapper.select:
select
from classroom_info where classroom_mark='1'
and classroom_name=#{classroomName}

mapper.insert:
insert into classroom_info (classroom_id, classroom_name,
classroom_max,
classroom_info, classroom_remark, classroom_mark
)
values (#{classroomId,jdbcType=INTEGER}, #{classroomName,jdbcType=VARCHAR},
#{classroomMax,jdbcType=INTEGER},
#{classroomInfo,jdbcType=VARCHAR}, #{classroomRemark,jdbcType=VARCHAR},
#{classroomMark,jdbcType=VARCHAR}
)
