mapper.select:
		select
		
		from staff_info where staff_state !='0'

		
			and staff_id=#{staffId,jdbcType=INTEGER}
		
		
			and staff_name=#{staffName}
		
		
			and staff_mobile_phone=#{staffMobilePhone}
		
		
			and user_number=#{userNumber}
		
		
			and user_passowrd=#{userPassowrd}
		
		
			and staff_state=#{staffState}
		

		order by staff_id desc

		select a.*,b.role_name
		from staff_info a left join role_info b on a.role_id = b.role_id and
		a.staff_state !='0'

		
			and a.staff_id=#{staffId,jdbcType=INTEGER}
		
		
			and a.staff_name=#{staffName}
		
		
			and a.staff_mobile_phone=#{staffMobilePhone}
		
		
			and a.staff_state=#{staffState}
		

		order by a.staff_id desc
